-- Creo il database canzoni
drop database if exists canzoni;
create database canzoni;
use canzoni;

create table brani(
                      ID int primary key auto_increment,
                      Titolo char (100),
                      Artista char (100),
                      Note varchar (500),
                      NoteDurata varchar (500),
                      Intervalli varchar (500),
                      URL varchar (100),
                      URLmp varchar (100)
);

;